import React, {Component} from "react";
import {Button, Col, Container, Form} from "react-bootstrap";
import axios from 'axios';
import './Profile_Details.css';


class CreateEditButton extends Component
{
    constructor(props) {
             super(props);
             this.state = {
                 id:'',
                 email: '',
                 fName: '',
                 lName: '',
                 dateOfBirth: '',
                 address: '',
                 phone: '',
                 cus_Id: this.props.match.params.id,

                 

             };

             this.onChange = this.onChange.bind(this);
             this.onSubmit = this.onSubmit.bind(this);
             this.ShowEditForm = this.ShowEditForm.bind(this);
             this.SaveDetail = this.SaveDetail.bind(this);
             this.changeNameHandler = this.changeNameHandler.bind(this);
             this.changeDesHandler = this.changeDesHandler.bind(this);
             this.changeDateHandler = this.changeDateHandler.bind(this);


         }

         onChange(e) {
             this.setState({[e.target.name]: e.target.value});
         }
         
         onSubmit(e) {
                e.preventDefault();
                const changeDetails =
                {
                    email: this.state.email.value,
                    fName:  this.state.fName.value,
                    lName:  this.state.lName.value,
                    dateOfBirth: this.state.dateOfBirth.value,
                    address: this.state.address.value,
                    phone: this.state.phone.value,
                   
                }
                alert("Personal Details successfully updated");
                console.log(changeDetails);

             }
    
    ShowEditForm(btnEdit) {
      var editForm = document.getElementById("editForm");
      if (btnEdit.value == "Edit") {
          editForm.style.display = "block";
          btnEdit.value = "";
      } else {
          editForm.style.display = "none";
          btnEdit.value = "Edit";
      }
  } 
    // saveProfileDetails(){
    //     axios.request({
    //         method:'PUT',
    //         url: `http://localhost:8080/api/users/${this.state.id}`,
    //     }).then(response =>{
    //         this.props.history.push('/');
    //     }).catch(err=> console.error(err));

    // } 
    fetchDatail() {

        let h = new Headers();

        let email = window.sessionStorage.getItem('email');
        const Cryptr = require('cryptr');
        const cryptr = new Cryptr('keyword');
        let encryptedString = window.sessionStorage.getItem('encrypted');
        const decryptedString = cryptr.decrypt(encryptedString);
        console.log(decryptedString);
        let encoded = window.btoa(email + ':' + decryptedString);
        let auth = 'Basic ' + encoded;
        h.append('Accept', 'application/json');
        h.append('Authorization', auth);

        fetch(url + '/' + this.state.appointmentIdentifier, {
            method: 'GET',
            headers: h
        })
            .then(res => res.json())
            .then(json => {
                this.setState({ 
                    id: json.id,
                    appointmentIdentifier: json.appointmentIdentifier,
                    appointmentName: json.appointmentName,
                    description: json.description,
                    appointmentDate: json.appointmentDate
                 });

            });
    }
    changeEmaileHandler = (event) => {
        this.setState({ email: event.target.value });
    }
    changeDesHandler = (event) => {
        this.setState({ description: event.target.value });
    }
    changeDateHandler = (event) => {
        this.setState({ appointmentDate: event.target.value });
    }

    SaveDetail(){
        let h = new Headers();
        let email = window.sessionStorage.getItem('email');
        const Cryptr = require('cryptr');
        const cryptr = new Cryptr('keyword');
        let encryptedString = window.sessionStorage.getItem('encrypted');
        const decryptedString = cryptr.decrypt(encryptedString);
        console.log(decryptedString);
        let encoded = window.btoa(email + ':' + decryptedString);
        let auth = 'Basic ' + encoded;
        h.append('Content-Type', 'application/json');
        h.append('Accept', 'application/json');
        h.append('Authorization', auth);
        
        fetch(url + '/', {
            method: 'post',
            headers: h,
            body: JSON.stringify({
                id: this.state.id,
                appointmentIdentifier: this.state.appointmentIdentifier,
                appointmentDate: this.state.appointmentDate,
                appointmentName: this.state.appointmentName,
                description: this.state.description
             })
        }).then(json => this.fetchData()).then(console.log(this.state))
    }
    componentDidMount() {
        this.fetchData();
    }

        render(){
               return (   
                 <Container >
                    <Button
                        id="btnEdit" type="Button" value="Edit"
                        onClick={(btnEdit) => this.ShowEditForm(btnEdit)} 
                        style={{ marginBottom: '10%' }}  className="Button">
                        Edit
                    </Button>
                    <hr />
                         <Form  id="editForm" style={{display: "none"}} onSubmit={this.onSubmit}>
                              <Form.Group controlId="formBasicEmail">
                              <Form.Label>Email address</Form.Label>
                              <input
                                  id="emailD"
                                  type="email"
                                  className="form-control form-control"
                                  name="email"
                                  value={this.state.email}
                                  onChange={this.onChange}
                              />
                              <Form.Text className="text-muted">
                              </Form.Text>
                          </Form.Group>
  
                          <Form.Group controlId="formBasicAddress">
                          <Form.Label>Address</Form.Label>
                              <input
                                  type="address"
                                  className="form-control form-control"
                                  name="address"
                                  value={this.state.address}
                                  onChange={this.onChange}
                              />
                          </Form.Group>
                          <Form.Group controlId="formBasicPhone">
                              <Form.Label>Phone number</Form.Label>
                                   <input
                                       type="phone"
                                       className="form-control form-control"
                                       name="phone"
                                       value={this.state.phone}
                                       onChange={this.onChange}
                                      />
                              </Form.Group>
                                  <Form.Row>
                                     <Form.Group controlId="formBasicFname">
                                          <Col>
                                          <Form.Label for="firstName">First Name</Form.Label>
                                          <input
                                              id="firstName"
                                              type="fName"
                                              className="form-control form-control"
                                              name="fName"
                                              value={this.setState.fName}
                                              onChange={this.onChange}
                                          />
                                          </Col> 
                                      </Form.Group>
                                          <Form.Group controlId="formBasicLame">
                                          <Col>
                                          <Form.Label for="lastName">Last Name</Form.Label>
                                              <input
                                              id= "lastName"
                                              type="LName"
                                              className="form-control form-control"
                                              name="lName"
                                              value={this.setState.lName}
                                              onChange={this.onChange}
                                            />
                                          </Col>
                                      </Form.Group>
                                      <Form.Group controlId="formBasicDob">
                                          <Col>
                                          <Form.Label>Date Of Birth</Form.Label>
                                              <input
                                              type="date"
                                              className="form-control form-control"
                                              name="dateOfBirth"
                                              value={this.state.dateOfBirth}
                                              onChange={this.onChange}
                                              />
                                          </Col>
                                      </Form.Group>
                                  </Form.Row>
                                    <Button
                                        id="btnSave" type="Button" value="save"
                                        style={{ marginBottom: '10%' }}  className="Button">
                                        Save
                                   </Button>
                                <br/>
                              <p/>
                           </Form>
                 </Container>
                 
               );
               
           }
}

         
      export default CreateEditButton;
