import React, {Component} from "react";
import {Button,  Container} from "react-bootstrap";
import * as BsIcons from 'react-icons/bs';
import * as IoIcons from 'react-icons/io';
import './EmployeesPage.css';
import CreateAddEmployeeButton from './CreateAddEmployeeButton.js';
import SidebarBusiness from "../../Layout/Sidebar/SidebarBusiness.js";



class EmployeesPage extends Component
{
    constructor(props) {
        super(props)

        this.state = {
            employees: [],
        }
        this.editEmployees = this.editEmployees.bind(this);
    }
    editEmployees(id) {
        this.props.history.push(`/editEmployee/${id}`);
    }
    
   

    render() {
        return (  
        <> 
            {/* <SidebarBusiness/> */}
            <div>
                <h1><IoIcons.IoMdPeople /> Employees List</h1>
                <table style={{ width: '700px' }} >
                    <thead>
                        <tr>
                            <td> Name</td>
                            <td> Job</td>
                            <td> Business Name </td>
                            <td> Working Hours</td>
                            <td></td>
                        </tr>
                    </thead>
                    <tbody>
                          {this.state.employees.map(emp => 
                             <tr
                                    
                                className='emply'
                                key={emp.appointmentIdentifier}
                                 >
                                
                                <td> {emp.employeeName}</td>
                                <td> {emp.employeesDate}</td> 
                                <td className='edt'> 
                                    <Button
                                        // href={`/appointment/${emp.employeeIdentifier}`}
                                        // onClick={() => this.editAppointment(emp.employeesIdentifier)}
                                        style={{ marginRight:'10px' }}>Edit
                                    </Button>
                                    <Button variant="danger" 
                                    // onClick={this.delete.bind(this, emp.employeesIdentifier)}
                                    >
                                    Delete
                                    </Button>
                                 </td>
                            </tr> 
                         )} 
                    </tbody>
                </table>
                <CreateAddEmployeeButton/>
            </div>
        </>
            
          )
          
    }
}

         
      export default EmployeesPage;
