                                    className="form-control form-control"
                                    name="email"
                                    value={this.state.email}
                                    onChange={this.onChange}
                                />
                                <Form.Text className="text-muted">
                                </Form.Text>
                            </Form.Group>

                            <Form.Group controlId="formBasicAddress">
                                <Form.Label>Address</Form.Label>
                                <input
                                    type="address"
                                    className="form-control form-control"
                                    name="address"
                                    value={this.state.address}
                                    onChange={this.onChange}
                                />
                            </Form.Group>

                            <Form.Group controlId="formBasicPhone">
                                <Form.Label>Phone number</Form.Label>
                                <input
                                    type="phone"
                                    className="form-control form-control"
                                    name="phone"
                                    value={this.state.phone}
                                    onChange={this.onChange}
                                />
                            </Form.Group>

                            <Form.Row>
                                <Col>
                                    <Form.Label>First Name</Form.Label>
                                    <input
                                        type="FName"
                                        className="form-control form-control"
                                        name="name"
                                        value={this.state.fName}
                                        onChange={this.onChange}
                                    />
                                </Col>
                                <Col>
                                    <Form.Label>Last Name</Form.Label>
                                    <input
                                        type="LName"
                                        className="form-control form-control"
                                        name="LName"
                                        value={this.state.lName}
                                        onChange={this.onChange}
                                    />
                                </Col>
                                <Col>
                                    <Form.Label>Date Of Birth</Form.Label>
                                    <input
                                        type="date"
                                        className="form-control form-control"
                                        name="dateOfBirth"
                                        value={this.state.dateOfBirth}
                                        onChange={this.onChange}
                                    />
                                </Col>
                            </Form.Row>
                            <br/>

                            <Button id="updateButton" type="submit">
                                Update
                            </Button>
                            <t>      </t>
                            <Button id="saveButton" type="submit" value="Submit">
                                Save
                            </Button>

                            <Form.Group controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <input
                                    type="password"
                                    className="form-control form-control"
                                    name="password"
                                    value={this.state.password}
                                />
                            </Form.Group>

                            <Form.Group>
                                <Form.Group controlId="updateBasicPassword">
                                    <Form.Label>Password</Form.Label>
                                    <Form.Control type="password" placeholder="New Password"/>
                                </Form.Group>
                                <Form.Group controlId="updateBasicPassword">
                                    <Form.Label>Reconfirm Password</Form.Label>
                                    <Form.Control type="password" placeholder="New Password"/>
                                </Form.Group>

                                <Button id="pButton " type="submit" value="Submit">
                                    Change password
                                </Button>
                            </Form.Group>
                        </Form>
                    </div>
                </div>
             </Container>
            );

        }

    }
     export default Profile_Details;


