import React, {Component} from "react";
import {Button, Col, Container, Form} from "react-bootstrap";
import CreateEditButton from './CreateEditButton'
import Sidebar from '../../Layout/Sidebar/Sidebar.js';
import axios from 'axios';
import './Profile_Details.css';
import urlAddress from "../../ip.json";

const url = 'http://'+urlAddress.ip+':8080/api/';
console.log(urlAddress.ip);



class Edit_Profile extends Component
{
    constructor(props) {
             super(props);
             this.state = {
                 id:'',
                 email: '',
                 fName: '',
                 lName: '',
                 dateOfBirth: '',
                 address: '',
                 phone: '',
                //  cus_Id: this.props.match.params.id,
             };

             this.onChange = this.onChange.bind(this);
             this.onSubmit = this.onSubmit.bind(this);
            //  this.ShowEditForm = this.ShowEditForm.bind(this);
            //  this.SaveDetail = this.SaveDetail.bind(this);
            //  this.changeNameHandler = this.changeNameHandler.bind(this);
            //  this.changeDesHandler = this.changeDesHandler.bind(this);
            //  this.changeDateHandler = this.changeDateHandler.bind(this);
         }

         onChange(e) {
             this.setState({[e.target.name]: e.target.value});
         }
         
         onSubmit(e) {
                e.preventDefault();
                const changeDetails =
                {
                    email: this.state.email.value,
                    fName:  this.state.fName.value,
                    lName:  this.state.lName.value,
                    dateOfBirth: this.state.dateOfBirth.value,
                    address: this.state.address.value,
                    phone: this.state.phone.value,
                   
                }
                alert("Personal Details successfully updated");
                console.log(changeDetails);

             }


  getProfileDetails(){
    let customer_Id = this.props.match.params.id;
    // this.props.history.push(url + `/users/${customer_Id}`);
    axios.get(url + `users/${customer_Id}`)
        .then(response =>{
            console.log(response)
            this.setState({
                id: response.data.id,
                fName: response.data.fName,
                lName: response.data.lName,
                email: response.data.email,
                address: response.data.address,
                phone: response.data.phone,
            }, ()=> {
                console.log(this.state);
            });
        })  
        .catch(error =>{
                console.log(error)
            })
}

    saveProfileDetails(){
        axios.request({
            method:'PUT',
            url: `http://localhost:8080/api/users/${this.state.id}`,
        }).then(response =>{
            this.props.history.push('/');
        }).catch(err=> console.error(err));

    }

    componentWillUnmount(){
        this.getProfileDetails();
    }
   

        render(){
               return (   
                 <container fluid style={{paddingLeft:'0rem', paddingRight:'0rem'}}>
                     <Sidebar/>
                    <div className="body">
                         <Form  id="editForm"  onSubmit={this.onSubmit}>
                              <Form.Group controlId="formBasicEmail">
                              <Form.Label>Email address</Form.Label>
                              <input
                                  id="emailD"
                                  type="email"
                                  className="form-control form-control"
                                  name="email"
                                  value={this.state.email}
                                  onChange={this.onChange}
                              />
                              <Form.Text className="text-muted">
                              </Form.Text>
                          </Form.Group>
  
                          <Form.Group controlId="formBasicAddress">
                          <Form.Label>Address</Form.Label>
                              <input
                                  type="address"
                                  className="form-control form-control"
                                  name="address"
                                  value={this.state.address}
                                  onChange={this.onChange}
                              />
                          </Form.Group>
                          <Form.Group controlId="formBasicPhone">
                              <Form.Label>Phone number</Form.Label>
                                   <input
                                       type="phone"
                                       className="form-control form-control"
                                       name="phone"
                                       value={this.state.phone}
                                       onChange={this.onChange}
                                      />
                              </Form.Group>
                                  <Form.Row>
                                     <Form.Group controlId="formBasicFname">
                                          <Col>
                                          <Form.Label for="firstName">First Name</Form.Label>
                                          <input
                                              id="firstName"
                                              type="fName"
                                              className="form-control form-control"
                                              name="fName"
                                              value={this.setState.fName}
                                              onChange={this.onChange}
                                          />
                                          </Col> 
                                      </Form.Group>
                                          <Form.Group controlId="formBasicLame">
                                          <Col>
                                          <Form.Label for="lastName">Last Name</Form.Label>
                                              <input
                                              id= "lastName"
                                              type="LName"
                                              className="form-control form-control"
                                              name="lName"
                                              value={this.setState.lName}
                                              onChange={this.onChange}
                                            />
                                          </Col>
                                      </Form.Group>
                                      <Form.Group controlId="formBasicDob">
                                          <Col>
                                          <Form.Label>Date Of Birth</Form.Label>
                                              <input
                                              type="date"
                                              className="form-control form-control"
                                              name="dateOfBirth"
                                              value={this.state.dateOfBirth}
                                              onChange={this.onChange}
                                              />
                                          </Col>
                                      </Form.Group>
                                  </Form.Row>
                                    <Button
                                        id="btnSave"  value="save"
                                        style={{ marginBottom: '10%' }}  className="Button"
                                        onClick={this.saveProfileDetails.bind(this, this.state.id)}
                                        href="/UserAppo">
                                        Save
                                   </Button>
                                <br/>
                              <p/>
                           </Form>
                        
                           </div>
                 </container> 
                 
               );
               
           }
}

         
      export default Edit_Profile;
