                console.log(response)
            })
            .catch(error =>{
                console.log(error);                         
            })
            window.sessionStorage.clear();
            window.location.reload(true);
        }        
  }

    
        render(){
               return (   
                 <Container >
                    <Button
                        id="btnDelete" type="Button"
                        value="Delete account"
                        variant="danger" 
                        style={{ marginRight: '0%' }}
                        className="Button"
                        onClick={() => this.deleteAccount()}
                        // href="/login"
                        >                         
                        Delete account
                    </Button>
                 </Container>
                 
               );
               
           }
}

         
      export default DeleteAccount;
