            //  this.changeDateHandler = this.changeDateHandler.bind(this);
         }

         onChange(e) {
             this.setState({[e.target.name]: e.target.value});
         }
         
         onSubmit(e) {
                e.preventDefault();
                const changeDetails =
                {
                    email: this.state.email.value,
                    fName:  this.state.fName.value,
                    lName:  this.state.lName.value,
                    dateOfBirth: this.state.dateOfBirth.value,
                    address: this.state.address.value,
                    phone: this.state.phone.value,
                   
                }
                alert("Personal Details successfully updated");
                console.log(changeDetails);

             }
    
    ShowEditForm(btnEdit) {
      var editForm = document.getElementById("editForm");
      if (btnEdit.value == "Edit") {
          editForm.style.display = "block";
          btnEdit.value = "";
      } else {
          editForm.style.display = "none";
          btnEdit.value = "Edit";
      }
  } 

  getProfileDetails(){
    let customer_Id = this.props.match.params.id;
    // this.props.history.push(url + `/users/${customer_Id}`);
    axios.get(url + `users/${customer_Id}`)
        .then(response =>{
            console.log(response)
            this.setState({
                id: this.response.data.id,
                fName: this.response.data.fName,
                lName: this.response.data.lName,
                email: this.response.data.email,
                address: this.response.data.address,
                phone: this.response.data.phone,
            }, ()=> {
                console.log(this.state);
            });
        })  
        .catch(error =>{
                console.log(error)
            })
}

    saveProfileDetails(){
        axios.request({
            method:'PUT',
            url: `http://localhost:8080/api/users/${this.state.id}`,
        }).then(response =>{
            this.props.history.push('/');
        }).catch(err=> console.error(err));

    }

