import React, { Component } from 'react';
import {Button, Col, Container, Form} from "react-bootstrap";
import CreateChangePassButton from './CreateChangePassButton'
import Sidebar from '../../Layout/Sidebar/Sidebar.js';
// import React, {usesetstate, useEffect} from React;
import axios from 'axios';
import './Profile_Details.css';
import './CreateEditButton.js';
import Header from '../../Layout/Navbar/Navbar';
// import SignUp from './Pages/SingUp/SignUp';
import ReactDOM from 'react-dom';
import { connect } from 'react-redux'
// import { toggleTodo } from '../actions'
// import TodoList from '../components/TodoList'
// import { VisibilityFilters } from '../actions'

// const url = 'http://'+urlAddress.ip+':8080/api/appointment/';
// console.log(urlAddress.ip)

     class Profile_Details extends Component{

        ShowEditForm = this.ShowEditForm.bind(this);

        state = {
            customerDetail: '',
    };
   
    componentDidMount(){
        axios.get('http://localhost:8080/api/users/3')

        axios.get('http://jsonplaceholder.typicode.com/users')
        axios.get('http://jsonplaceholder.typicode.com/posts/1')
            .then(response =>{
                console.log(response)
                this.setState({customerDetail: response.data});
            })
            .catch(error =>{
                console.log(error)
            })

    }

    
    ShowEditForm(btnEdit) {
        var editForm = document.getElementById("editForm");
        if (btnEdit.value == "Edit") {
            editForm.style.display = "block";
            btnEdit.value = "";
        } else {
            editForm.style.display = "none";
            btnEdit.value = "Edit";
        }
    }
    //  <input id="btnEdit" type="Button" value="Edit"
    //                 onClick={(btnEdit) => this.ShowEditForm(btnEdit)}
    //                 />}

    render () {
        return (
           
           <Container fluid style={{paddingLeft:'0rem', paddingRight:'0rem'}}>
                 <Sidebar/>
            
                <div className="body">
                <h1>Profile Details</h1>  
                    <h4>Personal information</h4>
                    <hr></hr>
                    <h6>First name</h6>
                     <h6 key={this.state.customerDetail.id}>{this.state.customerDetail.username} </h6>
                    <h6>Last name</h6>
                     <h6 key={this.state.customerDetail.id}>{this.state.customerDetail.username} </h6>
                    <h6>Email</h6>
                     <h6 key={this.state.customerDetail.id}>{this.state.customerDetail.username} </h6>
                    <h6>Phone number</h6>
                    <h6>Date of birth</h6>
                    <hr></hr>
                    <h4>Personal address</h4>
                    <h6 >Address</h6>
                    <h6>Suburb</h6>
                    <hr></hr>
    
                    <Button
                        id="btnEdit" type="Button" value="Edit"
                        onClick={(btnEdit) => this.ShowEditForm(btnEdit)} 
                        style={{ marginBottom: '10%' }}  className="Button">
                        Edit
                    </Button>
                    <hr />
                   <div id="editForm" style={{display: "none"}}>
                         Name:
                         <input type="text" id="txtName" />
                    </div>
                </div>
             {/* <CreateEditButton/> */}
          </Container>
            
        );
     }
         
       
}
    
 


     export default Profile_Details;
